         }
        //如果是普通部门则生成excle
        else {
			        $filename = "TJFYMX-$date-$dept_id.xls";
            $objWriter=$this->toExcel($token,$date);
            //输出到浏览器
            header("Pragma: public");
            header("Expires: 0");
